import React, { useCallback, useEffect, useState } from "react";

import MoviesList from "./components/MoviesList";
import AddMovie from "./components/AddMovie";
import "./App.css";
import { child, get, onValue, query, ref } from "firebase/database";
import { db } from "./config/db";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [addMovie, setAddMovie] = useState({});

  const fetchMovieHandler = useCallback(async () => {
    try {
      setIsLoading(true);

      setError(null);

      // const res = await fetch(
      //   "https://react-http-cd376-default-rtdb.firebaseio.com/movies.json"
      // );

      // if (!res.ok) {
      //   throw new Error("Something Worng!");
      // }

      //const data = await res.json();

      const loadedMovies = [];

      //const snapshot = await get(child(dbRef, "/movies"));

      let data;

      await onValue(query(ref(db, "movies/")), (snapshot) => {
        data = snapshot.val();
        console.log(data);
      });

      for (const key in data) {
        loadedMovies.push({ id: key, ...data[key] });
      }

      setMovies(loadedMovies);

      setIsLoading(false);
    } catch (err) {
      setError(err.message);
    }
    setIsLoading(false);
  }, []);

  const addMovieHandler = useCallback(async (movie) => {
    try {
      const res = await fetch(
        "https://react-http-cd376-default-rtdb.firebaseio.com/movies.json",
        {
          method: "POST",
          body: JSON.stringify(movie),
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      const data = await res.json();
      setAddMovie(movie);
      console.log(`AddMovieHandler POST Response Data : ${data}`);
    } catch (err) {
      console.log(`Error : ${err}`);
    }
  }, []);

  const readOne = async () => {
    const dbRef = ref(db);

    const snapshot = await get(child(dbRef, "/movies"));
    if (snapshot.exists()) {
      console.log(snapshot.val());
    }
  };

  useEffect(() => {
    fetchMovieHandler();
    //readOne();
  }, [fetchMovieHandler, db]);

  let content = <p>Found No Movies.</p>;

  if (movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loadding...</p>;
  }

  return (
    <React.Fragment>
      <section>
        <AddMovie onAddMovie={addMovieHandler} />
      </section>
      <section>
        <button onClick={fetchMovieHandler}>Fetch Movies</button>
      </section>
      <section>
        {/* {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
        {!isLoading && movies.length === 0 && !error && <p>Found No Movies.</p>}
        {!isLoading && error && <p>{error}</p>}
        {isLoading && <p>Loadding...</p>} */}
        {content}
      </section>
    </React.Fragment>
  );
}

export default App;
